@pytest.mark.skip()
def test_add_mve(): # ln of marketcap
    global sep_featured
    # AAPL at 1998-02-09
    sep_aapl_row = sep_featured.loc[(sep_featured.ticker == "AAPL") & (sep_featured.index == "1998-02-09")].iloc[0]
    datekey0 = sep_aapl_row["datekey"]
